sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
	var contactRecord = "";
	var userGR = new GlideRecord('x_808219_btfu_relationship_contact');
	userGR.addQuery('contact_name', input.sys_id);
	userGR.query();
	if (userGR.next()) {
		contactRecord = userGR.getValue('sys_id');
		userGR.setValue('favorite', true);
		userGR.update();
	}

	if (!contactRecord) { //if no contact record, create one before first
		var newRelationshipGR = new GlideRecord('x_808219_btfu_relationship_contact');
		newRelationshipGR.initialize();
		newRelationshipGR.name = gs.getUserID();
		newRelationshipGR.contact_name = input.sys_id;
		newRelationshipGR.favorite = true;
		contactRecord = newRelationshipGR.insert();
	}

})(input);
