sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    const {
        context,
        data,
        state,
        setState,
        emit
    } = api;

    const {
        snHttp
    } = helpers;

    const curentUser = api.context.session.user.sys_id;
    //Get the contact user information
    snHttp('/api/now/v1/table/sys_user/' + curentUser, {
        method: "GET",
        batch: false,
    }).then(response => {
        if (response.response.result) {
            //Update state
            setState('managerQuery', 'sys_id!=' + curentUser + '^manager=' + response.response.result.manager.value);
        }
    }).catch(error => {
        console.log('Error');
    });

}
